case class SentimentText(id: Int, label: Double, sentiment: String, text: String)

def parse(line: String): SentimentText = {
  val tokens = line.split(",")
  tokens(1) match {
    case "0" => SentimentText(tokens(0).toInt, 0.0, "NEGATIVE", tokens(3).trim)
    case "1" => SentimentText(tokens(0).toInt, 1.0, "POSITIVE", tokens(3).trim)
  }
}
